/* DP Services tab filter - updated June 10, 2024 */
  
          #tabbedSection {
              position: relative;
              margin: 0;
          }
          #life-cycles-container {
              width: 100%;
              max-width: 1200px;
              padding: 3rem 0 10rem 0;
              margin-inline: auto;
              --deloittegreen: #86BC25;
              --darkgreen: #05120b;
              --blue2: #62B5E5;
              --teal4: #00ABAB;
  
              @media (width <= 768px) {
                  padding: 3rem 0;
                  }            
  
              & * {
                  box-sizing: border-box;
              }
  
              & header{
                  width: 60%;
                  padding-inline: 1rem;
  
                  & h3 {
                      font-family: 'Open Sans Light', 'Open Sans';
                      font-size: 2.5rem;
                      font-style: normal;
                      font-weight: 300;
                      line-height: 2.7rem
                      letter-spacing: -0.025rem;
                      margin: 0 0 24px;
                  }
              }
  
              & h4:nth-of-type(1) {
                  flex-basis: 25%
              }
  
              & h4:nth-of-type(2) {
                  flex-basis: 50%
              }
  
              & .life-cycles {
                  margin-top: 2rem;
                  padding: 0 0 0 1rem;
                  display: flex;
                  gap: 2rem;
              }
  
              & .life-cycles__controls {
                  flex-basis: 25%;
                  border-right: 1px solid #cccccc;
                  padding-right: 1rem;
                  min-width: 170px;
                  max-width: 170px;
  
                  .controls__item {
                      --_theme-color: black;
                      padding: .5rem;
                      cursor: pointer;
                      transition: .1s;
                      border-left: 5px solid white;
                      color: #000000;
  
                      font-family: "Open Sans";
                      font-size: 1.125rem;
                      font-style: normal;
                      font-weight: 300;
  
                      &[aria-current="true"] {
                          font-weight: 700;
                          cursor: default;
                          border-color: var(--deloittegreen);
                          color: var(--deloittegreen);
  
                          &:hover {
                              text-decoration: none;
                          }
                      }
                      &::after {
                          display: block;
                          content: attr(title);
                          font-weight: bold;
                          height: 1px;
                          color: transparent;
                          overflow: hidden;
                          visibility: hidden;
                      }
                  }
                  @media (width <= 768px) {
                      flex-basis: 100%;
                      display: flex;
                      gap: 1rem;
                  }
              }
  
              .content__item {
                  display: none;
                  width: 55%;
  
                  &[aria-current="true"] {
                      display: block;
                  }
  
                  & .img-wrapper {
                      position: absolute;
                      right: 0;
                      top: 0;
                      bottom: 0;
                      background-size: contain;
                      background-position: center;
                      background-repeat: no-repeat;
                      width: 40%;
                  }
  
                  & h4 {
                      color: #26890D;
                      font-family: 'Open Sans Light', 'Open Sans';
                      font-size: 28px;
                      font-style: normal;
                      font-weight: 300;
                      line-height: 109.682%;
                      letter-spacing: -0.28px;
                      margin: 1rem 1.3rem;
                      color: var(--deloittegreen);
                  }
  
                  & p {
                      margin: 1.3rem 1.3rem;
                  }
  
                  & .link {
                      color: #000;
                      font-family: 'Open Sans';
                      font-size: 0.875rem;
                      font-style: normal;
                      font-weight: 700;
                      line-height: normal;
                      text-decoration: none;
                      margin: 0rem 1.3rem;
                  }
              }
  
              & ul {
                  list-style: disc outside;
                  margin-left: 1rem;
                  font-size: 1rem;
                  padding-left: 1rem;
  
                  & ul {
                      list-style: "–  " outside;
