	/* CONTACT */
 
	.contact {
		background-color: black;
		padding: 2rem 0 5rem;
	}
 
	.contact-container {
		max-width: var(--section-wide);
		margin: auto;
		color: white;
	}
 
	.contact h3 {
		margin-bottom: 3rem;
	}
 
	.contact h3:nth-of-type(2) {
		margin: 3rem 0;
	}
 
	.contact .cards {
		gap: 2rem;
	}
 
	.contact .card {
		width: 350px;
		display: flex;
		gap: 1rem;
	}
 
	.contact .card img {
		align-self: baseline;
		width: 90px;
	}
 
	.contact .card address {
		font-style: normal;
		font-size: var(--font-size-14);
		transform: translateY(-10px);
	}
 
	.contact .card address p {
		margin: 0.2rem 0;
		font-size: var(--font-size-14);
	}
 
	.contact .card address .profile-name {
		font-weight: 800;
	}
 
	.contact .card address a {
		display: block;
		text-decoration: none;
		margin: 0.2rem 0;
		color: white;
	}
 
	.contact .card address .profile-email {
		color: var(--green-text);
	}
 
	.contact .card address .profile-phone {
		color: white;
	}

	/* prevent full height in author mode */
	.cq-Editable-dom .hero {
		height: auto;
	}
 
	@media (min-width: 800px) {
		.flex-col {
			flex: 1;
		}
	}
 
	@media (max-width: 799px) {
		.root-layout section:not(.priorities, .hero){
			padding: var(--mobile-padding);
		}
 
		.intro-container {
			gap: 1rem;
		}
 
		.priorities-copy {
			padding: var(--mobile-padding);
		}
 
		.priorities-copy .top {
			margin-bottom: 1rem;
		}
 
		.priorities ul {
			column-count: 1;
			margin: 0 1rem 0 2rem;
			padding: 0;
		}
 
		.priorities ul li:nth-child(2) {
			padding-top: 0;
		}
 
		.priorities h2::before {
			display: none;
		}
 
		.ma-services::before {
			display: none;
		}
 
		.difference ul {
			column-count: 1;
			margin: 2rem 1rem;
			padding: 0;
		}
 
		.root-layout section.perspective {
			padding-top: 4rem;
		}
 
		.perspective .cards {
			gap: 1rem;
		}
 
		.perspective .card {
			min-height: 300px;
			width: 100%;
		}
 
		.dt-only {
			display: none;
		}
	}
 
	@media (min-width: 768px) and (max-width: 1024px) {
		.hero-image img {
			width: 80%;
			margin: auto;
		}
 
		.perspective .card {
			width: 300px;
		}
	}
 
	@media (max-width: 768px) and (orientation: landscape) {
		.hero-video__container {
			width: 60%;
			top: 40%;
		}
	}
 
	/* CAROUSEL */
 
	.carousel {
		width: 100%;
		aspect-ratio: 1920 / 674;
	}
 
	.carousel.card1-background {
		background: linear-gradient(90deg, rgba(0,0,0,1) 50%, rgba(0,0,0,0) 100%),
					url("/content/dam/html/us/mergers-acquisitions/hub/esg-banner.jpeg");
		background-size: cover;
		background-repeat: no-repeat;
		background-position: center 35%;
	}
 
	.carousel .carousel-card {
		display: none;
		max-width: var(--section-narrow);
		height: 100%;
		/* padding: 0px 14px; */
		margin: auto;
		flex-direction: column;
		justify-content: center;
		position: relative;
	}
 
	.carousel .carousel-card.active {
		display: flex;
	}
 
	.carousel .card-type {
		background-color: var(--green-link);
		display: inline-block;
		width: fit-content;
		padding: 0.5rem;
		font-size: var(--font-size-12);
		color: white;
		margin-bottom: 1rem;
	}
 
	.carousel .card-title {
		color: white;
		font-size: var(--font-size-32);
		font-weight: 600;
		line-height: 40px;
		max-width: 600px;
	}
 
	.carousel .card-subtitle {
		color: var(--green-link);
		font-size: var(--font-size-24);
		font-weight: 700;
		line-height: 36px;
		max-width: 600px;
	}
 
	.carousel .card-text {
		color: white;
		font-size: var(--font-size-16);
		max-width: 600px;
	}
 
	.carousel .card-cta {
		color: var(--green-link);
		font-size: var(--font-size-16);
		text-decoration: underline;
		max-width: 600px;
	}
 
	@media (max-width: 1172px) {
		.carousel {
			aspect-ratio: unset;
			background-position: center right;
			padding: var(--mobile-padding);
		}
		.carousel .carousel-card {
			padding-bottom: 64px;
		}
	}

document.addEventListener('DOMContentLoaded', marsHub);
	// ANALYTICS
	const WDA = new WebDevAnalytics('mars-hub');
	// YT Setup
	function onYouTubeIframeAPIReady() {
		createPlayer('5vSBGI64HU4', document.querySelector('#hero-video')?.id);
	}
	// DOMContentLoaded
	function marsHub() {
		document.querySelector('.hero__mouse-icon').addEventListener('click', scrollToLocation);
		// Open modal with both desktop and mobile buttons
		let heroVideoBackdrop = document.querySelector("#hero-video__backdrop");
		let heroVideoBtns = document.querySelectorAll(".hero__button");
 
		heroVideoBtns.forEach((btn) =>
			btn.addEventListener("click", function (e) {
				window.scroll(0, 0);
				heroVideoBackdrop.classList.add("video-visible");
				document.body.classList.add("modal-open");
				fullVideo.playVideo();
				(window.devicePixelRatio > 1 && !isMobile()) ? document.querySelector('.hero-video__container').style.width = '50%' : null;
				heroVideoBtnAnalytics();
			})
		);
 
		// Close modal when backdrop is clicked
		heroVideoBackdrop.addEventListener("click", function (e) {
			if (e.target == heroVideoBackdrop) {
				fullVideo.seekTo(0);
				fullVideo.pauseVideo();
				heroVideoBackdrop.classList.remove("video-visible");
				document.body.classList.remove("modal-open");
			}
		});
 
		document.querySelector('.contact-container').addEventListener('click', (e) => {
			let events;
			const section = 'contact-us';

			if (e.target.matches('.profile-name')) {
				const val = e.target.textContent.replace(' ', '-').toLowerCase();
				events = [
					{ event: 'event72', id: val, section },
					{ event: 'eVar72', content: val, section },
				];
			}

			if (e.target.matches('.profile-email')) {
				const val = e.target.textContent.replace(/[@.]/g, '');
				events = [
					{ event: 'event72', id: val, section },
					{ event: 'eVar72', content: val, section },
					{ event: 'event44'}
				];
			}

			WDA.dispatchAnalyticsEvent(events);
		});

		document.querySelector('.ma-services').addEventListener('click', (e) => {
			const section = 'ma-services';
			if (e.target.matches('.link')) {
				const val = e.target.textContent.replaceAll('&', '').replaceAll(' ', '-').replaceAll('--', '-').toLowerCase();
				const events = [
					{ event: 'event72', id: val, section },
					{ event: 'eVar72', content: val, section },
				];
				WDA.dispatchAnalyticsEvent(events);
			}
		});

		document.querySelector('.restructuring-services').addEventListener('click', (e) => {
			const section = 'restructuring-services';
			if (e.target.matches('.link')) {
				const val = e.target.textContent.replaceAll('&', '').replaceAll(' ', '-').toLowerCase();
				const events = [
					{ event: 'event72', id: val, section },
					{ event: 'eVar72', content: val, section },
				];
				WDA.dispatchAnalyticsEvent(events);
			}
		});

		function heroVideoBtnAnalytics() {
			WDA.dispatchAnalyticsEvent([
				{ event: 'event72', id: 'fullVideo', section: 'mars-hub-video' },
				{ event: 'eVar72', content: 'watch-the-video', section: 'mars-hub-video' },
				{ event: 'event23' }
			]);			
		}

	}

	function createPlayer(videoID, heroFrame) {
		fullVideo = new YT.Player(heroFrame, {
			videoId: videoID,
			playerVars: { autoplay: 0},
			events: { onStateChange: onPlayerStateChange },  
		});  
	}

	function scrollToLocation(e) {
		const location = e?.target?.hash;
		if (!location) { return; }

		e.preventDefault();
		window.scrollTo({ top: document.querySelector(location).offsetTop - 90, behavior: 'smooth'});
	}

