	function onPlayerStateChange(event) {
		if (event.data == -1) {
			WDA.dispatchAnalyticsEvent([
				{ event: 'eVar28', content: 'video-start', section: 'watch-the-video' },
				{ event: 'event17' }
			], ['prop4', 'prop5']);

		} else if (event.data == 0) {
			WDA.dispatchAnalyticsEvent([
				{ event: 'eVar28', content: 'video-end', section: 'watch-the-video' },
				{ event: 'event20' }
			], ['prop4', 'prop5']);
		}
	}

