                        display: flex;
                        align-items: center;
                    }

                    .category.hide {
                        display: none;
                    }

                    .read-time {
                        font-family: 'Open Sans';
                        font-size: 14px;
                        font-weight: 400;
                        line-height: 20px;
                        letter-spacing: 0px;
                        text-align: left;
                        text-wrap: nowrap;
                    }

                    .read-time.hide {
                        display: none;
                    }
