        function setupVerticalTiles() {
            var verticalTileContainer = document.getElementById('vertical_flipping_tiles_frag').getElementsByClassName('vertical-tile-container')[0]

            var tileTemplate = document.getElementsByClassName('post-wrap')[0]
            tileTemplate.style.display = 'none'

            vertical_flipping_tiles_frag_data.forEach(function (data) {
                var newTile = tileTemplate.cloneNode(['deep'])
                setTileData(newTile, data)
                verticalTileContainer.appendChild(newTile)
            })
        }

        function setTileData(el, data) {
            el.style = ''
            el.getElementsByTagName('h4').forEach(function (e) {
                if (e.classList.contains('back-title') && data.titleUrlPath) {
                    e.innerHTML = '<a target="_blank" href="' + data.titleUrlPath + '">' + data.titleText + '</a>'
                } else {
                    e.innerHTML = data.titleText
                }
            })
            el['dataset']['analytics_text'] = data.titleText + ':flipped'
            el.getElementsByClassName('post-front-text')[0].innerHTML = data.frontText
            el.getElementsByClassName('post-back-text')[0].innerHTML = data.backText
            el.getElementsByClassName('bullet-points-title')[0].innerHTML = data.bulletPointsTitle

            var backBulletsContainer = el.getElementsByClassName('back-bullet-points')[0]

            if (data.bulletPointOne) {
                var li = document.createElement('li')

                if (data.bulletPointOneUrlPath) {
                    li.innerHTML = '<a target="_blank" href="' + data.bulletPointOneUrlPath + '">' + data.bulletPointOne + '</a>'
                } else {
                    li.innerHTML = data.bulletPointOne
                }

                // li.innerHTML = data.bulletPointOne;
                backBulletsContainer.appendChild(li)
            }
            if (data.bulletPointTwo) {
                var li = document.createElement('li')

                if (data.bulletPointTwoUrlPath) {
                    li.innerHTML = '<a target="_blank" href="' + data.bulletPointTwoUrlPath + '">' + data.bulletPointTwo + '</a>'
                } else {
                    li.innerHTML = data.bulletPointTwo
                }

                // li.innerHTML = data.bulletPointTwo;
                backBulletsContainer.appendChild(li)
            }

            if (data.bulletPointThree) {
                var li = document.createElement('li')

                if (data.bulletPointThreeUrlPath) {
                    li.innerHTML = '<a target="_blank" href="' + data.bulletPointThreeUrlPath + '">' + data.bulletPointThree + '</a>'
                } else {
                    li.innerHTML = data.bulletPointThree
                }

                // li.innerHTML = data.bulletPointThree;
                backBulletsContainer.appendChild(li)
            }

            if (data.bulletPointFour) {
                var li = document.createElement('li')

                if (data.bulletPointFourUrlPath) {
                    li.innerHTML = '<a target="_blank" href="' + data.bulletPointFourUrlPath + '">' + data.bulletPointFour + '</a>'
                } else {
                    li.innerHTML = data.bulletPointFour
                }

                // li.innerHTML = data.bulletPointThree;
                backBulletsContainer.appendChild(li)
            }

            el.addEventListener('click', function (event) {
                if (event.target.tagName == 'A') {
                    return
                } else {
                    triggerAnalytics(event)
                    if (el.classList.contains('flipped')) {
                        el.classList.remove('flipped')
                    } else {
                        el.classList.add('flipped')
                    }
                }
            })
        }

        function enableHeaderSection() {
            if ('yes' == 'yes' || 'yes' == 'true') {
                var header = document.getElementById('vertical_flipping_tiles_frag_flipping_tiles_header')
                header.style.display = 'block'
            }
        }

        function setupAnalytics() {
            $('.vertical_flipping_tiles_frag a').click(triggerAnalytics)
        }
    })

