	/* PERSPECTIVE */
 
	.perspective {
		background-color: black;
		padding: 6rem 0 4rem;
		border-bottom: 5px solid transparent;
		border-image: url('/content/dam/html/us/mergers-acquisitions/hub/contact-border.png') 5;
		border-width: 0 0 5px 0;
		margin-top: -50px;
	}
 
	.perspective-container {
		max-width: var(--section-wide);
		margin: auto;
	}
 
	.perspective h3 {
		color: white;
		margin-bottom: 2rem;
	}
 
	.perspective .cards {
		gap: 0.5rem;
	}
 
	.perspective .card {
		background-color: white;
		min-height: 350px;
		width: 380px;
		padding: 1.5rem;
	}
 
	.perspective .badge {
		background-color: var(--green-link);
		display: inline-block;
		padding: 0.5rem;
		font-size: var(--font-size-12);
		color: white;
		margin-bottom: 1rem;
	}
 
	.perspective .card p {
		font-size: var(--font-size-14);
	}
 
	.perspective .card a {
		color: var(--green-link);
		text-decoration: underline;
		font-size: var(--font-size-14);
	}
 
	/* CONTACT */
 
	.contact {
		background-color: black;
		padding: 2rem 0 5rem;
	}
 
	.contact-container {
		max-width: var(--section-wide);
		margin: auto;
		color: white;
	}
 
	.contact h3 {
		margin-bottom: 3rem;
	}
 
	.contact h3:nth-of-type(2) {
		margin: 3rem 0;
	}
 
	.contact .cards {
		gap: 2rem;
	}
 
	.contact .card {
		width: 350px;
		display: flex;
		gap: 1rem;
	}
 
	.contact .card img {
		align-self: baseline;
		width: 90px;
	}
 
	.contact .card address {
		font-style: normal;
		font-size: var(--font-size-14);
		transform: translateY(-10px);
	}
 
	.contact .card address p {
		margin: 0.2rem 0;
		font-size: var(--font-size-14);
	}
 
	.contact .card address .profile-name {
		font-weight: 800;
	}
 
	.contact .card address a {
		display: block;
		text-decoration: none;
		margin: 0.2rem 0;
		color: white;
	}
 
	.contact .card address .profile-email {
		color: var(--green-text);
	}
 
	.contact .card address .profile-phone {
		color: white;
	}

	/* prevent full height in author mode */
	.cq-Editable-dom .hero {
		height: auto;
	}
 
	@media (min-width: 800px) {
		.flex-col {
			flex: 1;
		}
	}
 
	@media (max-width: 799px) {
