		var assetsTitleContainer = assetsContainer.querySelector(".js-sce-assets-title");
		var assetsBlocksContainer = assetsContainer.querySelector(".js-sce-assets-grid");

		// Set Title
		if(assetsTitle != "" && assetsTitle.charAt(0) != "$") {
			assetsTitleContainer.innerText = assetsTitle;
		} else {
			assetsTitleContainer.remove();
		}

		// Set Articles
		if(sceParseJson(assetsBlocks)) {
			for(var blockObj of JSON.parse(assetsBlocks)) {
				var assetBlock = document.createElement("div");
				assetBlock.classList.add("sce-assets__block");
				var assetBlockContent = '';
				if(blockObj.title) {
					assetBlockContent += "<h3 class=\"sce-assets__block-title\">" + blockObj.title + "</h3>";
				}
				if(blockObj.logos) {
					var logosContainer = "<div class=\"sce-assets__block-logos\">";
					for(var blockLogo of blockObj.logos.split('|')) {
						logosContainer += "<div class=\"sce-assets__block-logo\"><img src=\"" + blockLogo + "\" /></div>";
					}
					logosContainer += "</div>";
					assetBlockContent += logosContainer;
					assetBlock.classList.add("sce-assets__block--logos");
				}
				else if(blockObj.scrollText) {
					assetBlockContent += "<div class=\"sce-assets__block-scroll\"><span>" + blockObj.scrollText + " " + blockObj.scrollText + "</span></div>";
					assetBlock.classList.add("sce-assets__block--scroll");
				}
				assetBlock.innerHTML = assetBlockContent;

				var assetToggle = document.createElement("button");
				assetToggle.classList.add("sce-assets__block-toggle");
				assetToggle.setAttribute("type", "button");
				assetToggle.setAttribute("aria-label", "Toggle Motion");
				assetToggle.addEventListener("click", function(e) {
					e.preventDefault();
					this.parentElement.classList.toggle("is-paused");
				});
				assetBlock.appendChild(assetToggle);

				assetsBlocksContainer.appendChild(assetBlock);
			}
		}

		// Rotate logos
		for(var assetLogosContainer of assetsBlocksContainer.querySelectorAll(".sce-assets__block-logos")) {
			var assetLogos = assetLogosContainer.querySelectorAll(".sce-assets__block-logo");
			var activeLogo = assetLogos[0];
			activeLogo.classList.add("is-open");

			setInterval( () => {
				if(!assetLogosContainer.parentElement.classList.contains("is-paused")) {
					activeLogo.classList.remove("is-open");
					if(activeLogo.nextElementSibling) {
						activeLogo = activeLogo.nextElementSibling;
					} else {
						activeLogo = assetLogos[0];
					}
					activeLogo.classList.add("is-open");
				}
			}, 3000)
		}
	}

