document.addEventListener('DOMContentLoaded', function () {
    var sCtaId = "scestickycta";
    var sCtaContainer = document.getElementById(sCtaId);
    if (sCtaContainer) {
      var sCtaLinkUrl = "https://my.deloitte.com/?site=us-en&action=optin&aoi=a095Y00001DwN7vQAF&pl=en-US&pc=US&pi=int";
      var sCtaLinkLabel = "Sign up for sustainability insights and innovations";

      var sCtaBtnContainer = sCtaContainer.querySelector(".js-sce-sticky-cta-btn");

      // Set Button
      if (sCtaLinkUrl != "" && sCtaLinkUrl.charAt(0) != "$" && sCtaLinkLabel != "" && sCtaLinkLabel.charAt(0) != "") {
        sCtaBtnContainer.href = sCtaLinkUrl;
        sCtaBtnContainer.querySelector("span").innerText = sCtaLinkLabel;
      } else {
        sCtaContainer.remove();
      }

      // Sticky behavior
      var navFragment = $(sCtaContainer).parents(".htmlfragment");
      setTimeout(() => {
        navFragment[0].style.height = sCtaContainer.getBoundingClientRect().height + 'px';
      }, 500);

      var headerElem = document.getElementById("header");
      var headerHeight = headerElem.getBoundingClientRect().height;

      const isMobile = window.matchMedia('(max-width: 500px)').matches;

      function toggleStickyNav() {
        var scrollThreshold = navFragment[0].getBoundingClientRect().top;
        if (scrollThreshold <= headerHeight) {
          sCtaContainer.style.position = 'fixed';

          if (isMobile) {
            const isScrollUp = document.querySelector('body').classList.contains('is-scroll-up');
            sCtaContainer.style.top = isScrollUp ? (headerHeight - 1) + 'px' : '0px';
          } else {
            sCtaContainer.style.top = (headerHeight - 1) + 'px';
          }

        } else {
          sCtaContainer.style.position = '';
          sCtaContainer.style.top = '';
        }

        if (headerElem.style.position == "relative") {
          sCtaContainer.classList.add("is-header-hidden");
        } else {
          sCtaContainer.classList.remove("is-header-hidden");
        }

        for (var staticCta of document.querySelectorAll(".sce-static-cta")) {
          var staticBounds = staticCta.getBoundingClientRect();
          if (staticBounds.top < window.innerHeight - staticBounds.height) {
            sCtaContainer.classList.add("is-hide");
          } else {
            sCtaContainer.classList.remove("is-hide");
          }
        }
      }

      window.addEventListener("scroll", toggleStickyNav);
      toggleStickyNav();
    }
  });

