        #usopc .navbar-mobile .icon-chevron { padding-left: 12px; font-size: 1rem; margin-top: 3px; transform: rotate(0deg); transform-origin: center; transition: transform 0.5s ease-in-out; }
        
        #usopc .navbar-mobile .icon-chevron::before { content: "\f078";}
        #usopc .navbar-mobile .rotated .icon-chevron::before { transform: rotate(180deg); }

    #usopc header {  min-height: 600px; display: flex; flex-direction: column; position: relative; }

    /* #usopc header { background-image: none; } */
