        #usopc header .mobile-video:after { content: ''; position: absolute; z-index: 4; left: 0; right: 0; top: 0; bottom: 0; background: rgb(2, 0, 36); background: linear-gradient(0deg, rgba(2, 0, 36, 1) 0%, rgba(0, 0, 0, 1) 25%, rgba(0, 0, 0, 0.45) 100%); opacity: 0.7; mix-blend-mode: multiply; }
