@charset "UTF-8";
    #fsi-banner-frag{
        position: sticky;
        top: 0;
        z-index: 1 !important;
    }
    /* #fsi-stickynav, #fsi-video-cta, #fsi-services-frag, #fsi-serve-frag, #fsi-stories-frag, #fsi-quotes-frag{
        z-index: 2;
    } */
    .htmlfragment {
        z-index: 2;
        position: relative;
    }
    #fsi-services-section{
        background-color: white;
    }
    #fsi-stories-frag{
        background-color: white;
    }
    #fsi-latest-insight{
        background-color: white;
    }
    .cq-placeholder, .new{
        display: none !important;
    }
    .branded-landing-page-content {
        max-width: 100% !important;
        width: 100% !important;
        margin: 0 !important;
        margin-top: 20px;
    }

    .branded-landing-page-content .aem-GridColumn {
        padding-left: 0 !important;
        padding-right: 0 !important;
    }
    .fsi-hub-banner {
        background-color: black;
        height: 100dvh;
        max-height: 1600px;
        display: flex;
        justify-content: center;
        align-items: center;
        overflow: hidden;
        position: relative;
    }

    .fsi-hub-banner .standard-white-component {
        max-width: unset;
    }

    .fsi-hub-banner .wip .container-fluid {
        max-width: 1200px;
        width: 100%;
        padding-right: 0px;
        padding-left: 15px;
        padding-top: 100px;
        padding-bottom: 100px;
        margin-left: auto;
        margin-right: auto;
        display: flex;
        height: 600px;
        justify-content: center;
        align-items: center;
        /* gap: 10px; */
        align-self: stretch;
    }

    .fsi-hub-banner .full .container-fluid {
        max-width: 1200px;
        
    }

    .fsi-hub-banner .container-fluid .conten-container{
        display: flex;
        flex-direction: column;
        justify-content: center;
        align-items: flex-start;
        flex: 1 0 0;
    }
    .fsi-hub-banner .container-fluid .conten-container h1{
        color: var(--White, #FFF);
        text-shadow: 0px 4px 4px rgba(0, 0, 0, 0.25);
        font-family: "Open Sans";
        font-size: 63px;
        font-style: normal;
        font-weight: 300;
        line-height: 115%; /* 73.6px */
        max-width: 600px;
    }

    .fsi-hub-banner .container-fluid .conten-container p{
        color: var(--White, #FFF);
font-feature-settings: 'clig' off, 'liga' off;
font-family: "Open Sans";
font-size: 30px;
font-style: normal;
font-weight: 400;
line-height: normal;
margin-block: 0;
margin-top:40px;
text-wrap: nowrap;
    }

    .scroll-to-btm{
        display: flex;
        max-width: 1200px;
        width: 100%;
        align-items: flex-end;
        position: absolute;
        align-items: center;
        column-gap: 20px;
        padding-left: 15px;
        bottom: 10%;
        transition: all 0.2s linear;
        opacity: 0;
    }
    .scroll-to-btm-animation{
        bottom: 5%;
        opacity: 1;
    }
    .scroll-cta{
        display: flex;
        align-items: center;
        column-gap: 20px;   
        cursor: pointer;
    }
    .scroll-to-btm:hover{
        bottom: 6%;
    }
    .scroll-to-btm:hover svg .scroll-cta-svg-border{
        stroke: #86BC25;

    }
    .scroll-cta-svg-fill{
        fill:#26890D;
    }
    .scroll-to-btm:focus svg .scroll-cta-svg-fill{
        fill: #046A38 !important;
    }
    .scroll-to-btm p{
        color: var(--White, var(--White, #FFF));
font-family: "Open Sans";
font-size: 16px;
font-style: normal;
font-weight: 400;
line-height: 20px; /* 125% */
margin-block: 0px;
    }
    @media screen and (max-width:720px) {
        .fsi-hub-banner .container-fluid .conten-container {
            box-sizing: border-box;
            padding-left: 15px;
            padding-right: 15px;
        }
        .fsi-hub-banner .container-fluid img{
            width: 100% !important;
        }
        .fsi-hub-banner .container-fluid .conten-container h1{
            font-size: 48px;
        }
        .fsi-hub-banner .container-fluid .conten-container p{
            font-size: 22px;
            margin-top:22px;
            text-wrap: wrap;
        }
        .fsi-hub-banner .full .container-fluid {
            flex-direction: column-reverse;
            height: auto;
        }
        .fsi-hub-banner .wip .container-fluid{
            padding-top:20px;
        }
        .dmod-swiper__counter{
            margin-top:4px !important;
        }
        .leader{
            border-left:none !important;
        }
        #fsi-latest-insight{
            padding-top:80px !important;
            padding-bottom: 80px !important;
        }
        #fsi-latest-insight header{
            display: none;
        }
        #fsi-banner-frag{
            position: relative;
        }
        .scroll-to-btm{
            position: relative;
        }
        .fsi-hub-banner{
            height: auto;
            padding-bottom: 32px;
            display: block;
            padding-top: 32px;
        }
        .fsi-hub-banner .wip .container-fluid{
            padding-bottom: 35px;
            padding-left: 0px;;
        }
    }
   

    #leadership{
        background-color: #F5F5F5;
    }
    .fsi-leaders{
        max-width: 1200px !important;
    }

    .dmod-swiper-carousel--v1{
        padding-block: 0px !important;
        padding-bottom: 101px !important;
        padding-top: 60px !important;
    }
    .dmod-swiper-carousel--v1 .dmod-swiper__cta{
        padding:11px 40px !important;
    }

