    function generateRecentReports(reports) {
        const container = document.getElementById('services-container');
        container.innerHTML = '';

        reports.forEach(report => {
            const colDiv = document.createElement('div');
        colDiv.className = 'col-md-4';
        const cardShadowColor = report.borderColor; // Get borderColor for the shadow
        const cardHoverShadowColor = report.borderColor; // Define hover shadow color, you might want to adjust this
        
        // Create a card element
        const card = document.createElement('a');
        card.setAttribute('target', '_blank');
        card.href = report.ctaLink;
        card.className = 'card';
        card.innerHTML = `
            <div>
                <h3>${report.title}</h3>
                <p>${report.desc}</p>
            </div>
            <p class="link">${report.ctaLabel} <svg xmlns="http://www.w3.org/2000/svg"
                    width="24" height="24" viewBox="0 0 24 24" fill="none">
                    <path
                        d="M4 11V13H16L10.5 18.5L11.92 19.92L19.84 12L11.92 4.07996L10.5 5.49996L16 11H4Z"
                        fill="#007CB0" />
                </svg></p>
            <div class="decoration" style="background-color:${report.borderColor}"></div>
        `;
        
        // // Apply initial box-shadow with borderColor
        // card.style.boxShadow = `4px 0 0 0 ${cardShadowColor} inset, 4px 4px 4px 0 rgba(167, 168, 170, 0.25)`;
        
        // // Handle hover effect dynamically
