    ctaLink:"#stories-module"
  },
  {
    ctaLabel: "Client impact",
    ctaLink:"#story-1"
  }

],
      }

      const ele = $("#im-banner-banner");
      const headingEle =   branded_header_date.heading && branded_header_date.heading[0] !== "$" ? `<h1 style="color:black"> ${branded_header_date.heading} </h1>` : "";
      const descEle =   branded_header_date.desc && branded_header_date.desc[0] !== "$" ? `<h2> ${branded_header_date.desc} </h2>` : "";
      ele.append(
          `
          <div>
              ${headingEle}
              ${descEle}
              </div>
          `
      );
      ele.append(
          `
          <picture>
                      <source media="(min-width:1200px)" srcset="${branded_header_date.desktopImg}" />
                      <img src="${branded_header_date.mobileImg}" alt="..." width="auto"/>
                  </picture>
          `
      )

      const bannerLinkContainer = $("#im-banner-banner-links");
      branded_header_date.links.forEach((val) => {
          const linkElement = document.createElement('a');
          linkElement.href = val.ctaLink;

          linkElement.title = val.ctaLabel;
          linkElement.textContent = val.ctaLabel;

          if(val.ctaLink[0] !== '#'){
              linkElement.target = "_blank";
          }

          linkElement.addEventListener('click', (e) => {
              trackLinkClick(e.target.title);
              if (e.target.href.startsWith('#')) {
                  e.preventDefault();
                  const targetId = e.target.getAttribute('href')
                  const targetElement = document.getElementById(targetId);
                  if (targetElement) {
                      document.getElementById(targetId).scrollIntoView({
                          behavior: 'smooth'
                          })
                  }
              }
          });

          bannerLinkContainer.append(linkElement)
      });


  })
