          `
      )

      const bannerLinkContainer = $("#im-banner-banner-links");
      branded_header_date.links.forEach((val) => {
          const linkElement = document.createElement('a');
          linkElement.href = val.ctaLink;

          linkElement.title = val.ctaLabel;
          linkElement.textContent = val.ctaLabel;

          if(val.ctaLink[0] !== '#'){
              linkElement.target = "_blank";
          }

          linkElement.addEventListener('click', (e) => {
              trackLinkClick(e.target.title);
              if (e.target.href.startsWith('#')) {
                  e.preventDefault();
                  const targetId = e.target.getAttribute('href')
                  const targetElement = document.getElementById(targetId);
                  if (targetElement) {
                      document.getElementById(targetId).scrollIntoView({
                          behavior: 'smooth'
                          })
                  }
              }
          });

