/*========== 
    Modal window 
    ============*/

    body.modal-open {
        height: 100vh;
        overflow-y: hidden !important;
    }

    #quilt .modal {
        overflow-y: hidden;
        background-color: rgba(0, 0, 0, 0.5) !important;
        margin: auto;
        z-index: 2000;
        box-sizing: border-box;
    }

    #quilt .modal .modal-content {
        box-sizing: border-box;
        margin: 0 auto;
        position: relative;
        height: auto;
        width: 90%;
    }

    /* bootstrap overrides */

    #quilt {
        background: #f2f2f2;
        --main-color: white;
        font-size: 10px;
    }

    button:focus {
        outline: none;
    }

    .modal .modal-dialog {
        width: 750px !important;
        height: auto !important;
        margin: 0 auto !important;
    }

    .modal.in .modal-dialog {
        -webkit-transform: translate(0, 50px);
        -ms-transform: translate(0, 50px);
        transform: translate(0, 50px);
        width: 100%;
        height: auto;
    }

    /*==================== 
    FILTER SECTION classes 
    ======================*/
    .quilt__border {
        width: 100%;
    }

    .quilt__border:nth-of-type(2) {
        rotate: 180deg;
    }

    .quilt__header,
    .quilt__subheader {
        text-align: center;
    }

    #quilt h3.quilt__header {
        font-size: 2rem;
        font-weight: 400;
    }

    .quilt__filter-container {
        width: min(100%, 1200px);
        margin-inline: auto;
        margin-block: 0;
        padding-inline: 14px;
    }

    .quilt__filter__buttons-container {
        margin-bottom: 3em;
        display: flex;
        flex-flow: row nowrap;
        justify-content: center;
    }

    #quilt-filters {
        display: flex;
        flex-flow: row wrap;
        gap: 1em 0;
        width: 100%;
        max-width: 95%;
        justify-content: center;
        align-items: center;
        height: auto;
    }

    .quilt__filter__buttons-container__header {
        display: inline-block;
        color: #97999b;
        font-size: 1.1em;
        transition: all 0.5s;
        padding: 1em;
    }

    /* added to sticky */
    #quilt-filters.fix-menu {
        position: fixed;
        top: 0;
        z-index: 99;
        background: #f3f4f2;
        padding-bottom: 2em;
        width: 100%;
        --webkit-box-shadow: 0px 3px 11px -4px rgba(0, 0, 0, 0.75);
        -moz-box-shadow: 0px 3px 11px -4px rgba(0, 0, 0, 0.75);
        box-shadow: 0px 3px 11px -4px rgba(0, 0, 0, 0.75);
    }

    .fix-menu .quilt__filter__buttons-container__header {
        font-size: 1.4em;
    }

    .btn-filter {
        border-width: 0 1px 0 0;
        border-style: solid;
        border-color: black;
        color: #75787b;
        padding: 1em 1.5em;
        transition: 1s;
        width: auto;
        font-size: 1.4em;
        cursor: pointer;
        background: transparent;
    }

    .btn-filter:hover {
        text-decoration: underline;
        background: white;
    }

    .btn-filter.selected {
        font-weight: bold;
        color: #fff;
        background: #0076a8;
        cursor: default;
    }

    .btn-filter.selected:hover {
        cursor: default;
        text-decoration: none;
        border-right: none;
    }

