            -moz-box-shadow: 0px 3px 11px -4px rgba(0, 0, 0, 0.75);
            box-shadow: 0px 3px 11px -4px rgba(0, 0, 0, 0.75);
        }

        /* mobile menu */
        #quilt .modal .modal-body {
            display: block;
        }

        #quilt .modal-body p,
        #quilt .modal-body li {
            width: 100%;
            font-size: 1.2em;
        }

        .modal button.close {
            height: 28px;
            width: 28px;
            top: 3px !important;
            right: 3px !important;
        }

        /* begin sticky side menu */
        #quilt-stickymenu {
            color: white;
            width: 100%;
            background: #f3f4f2;
            padding-bottom: 0.4em;
            cursor: pointer;
            z-index: 998;
            display: block;
            max-height: 0;
        }

        .stickymenu_copy {
            font-size: 2em;
            font-weight: bold;
            width: 100%;
            position: relative;
            padding-left: 1em;
            color: black;
        }

        .stickymenu_buttons {
            height: auto;
            display: none;
            opacity: 0;
            position: relative;
            bottom: 6px;
            transition: all 5s;
            flex-flow: column;
            list-style-type: none;
            background: #f3f4f2;
            padding-bottom: 1em;
            -webkit-box-shadow: 0px 8px 11px -10px rgba(0, 0, 0, 0.75);
            -moz-box-shadow: 0px 8px 11px -10px rgba(0, 0, 0, 0.75);
            box-shadow: 0px 8px 11px -10px rgba(0, 0, 0, 0.75);
        }

        .grid-tiles__item {
            height: 300px;
        }

        .grid-tiles__item__copy--full-width,
        .grid-tiles__item__copy--one-third,
        .grid-tiles__item__copy--two-thirds {
            padding: 3.5em 0 2.5em 4em;
        }

        .opened {
            display: flex;
            opacity: 1;
        }

        .btn-filter {
            border-right: none;
        }

        div[class^="story"],
        div[class*="story"] {
            background-repeat: no-repeat;
            background-size: cover;
        }
    }

    /* ipad portrait */

    @media only screen and (min-width: 768px) and (max-width: 1024px) and (orientation: portrait) {
        .modal {
            overflow-y: auto !important;
        }

        .btn-filter {
            height: 55px;
        }

        .modal button.close {
            top: 4px !important;
            right: 2px !important;
        }
    }

