/* Slider */
	.slick-slider {
		position: relative;

		display: block;
		box-sizing: border-box;

		-webkit-user-select: none;
		-moz-user-select: none;
			-ms-user-select: none;
				user-select: none;

		-webkit-touch-callout: none;
		-khtml-user-select: none;
		-ms-touch-action: pan-y;
			touch-action: pan-y;
		-webkit-tap-highlight-color: transparent;
	}

	.slick-list {
		position: relative;

		display: block;
		overflow: hidden;

		margin: 0;
		padding: 0;
	}
	.slick-list:focus {
		outline: none;
	}
	.slick-list.dragging {
		cursor: pointer;
		cursor: hand;
	}

	.slick-slider .slick-track,
	.slick-slider .slick-list {
		-webkit-transform: translate3d(0, 0, 0);
		-moz-transform: translate3d(0, 0, 0);
			-ms-transform: translate3d(0, 0, 0);
			-o-transform: translate3d(0, 0, 0);
				transform: translate3d(0, 0, 0);
	}

	.slick-track {
		position: relative;
		top: 0;
		left: 0;

		display: block;
		margin-left: auto;
		margin-right: auto;
	}
	.slick-track:before,
	.slick-track:after {
		display: table;

		content: '';
	}
	.slick-track:after {
		clear: both;
	}
	.slick-loading .slick-track {
		visibility: hidden;
	}

	.slick-slide {
		display: none;
		float: left;

		height: 100%;
		min-height: 1px;
	}
	[dir='rtl'] .slick-slide {
		float: right;
	}
	.slick-slide img {
		display: block;
	}
	.slick-slide.slick-loading img {
		display: none;
	}
	.slick-slide.dragging img {
		pointer-events: none;
	}
	.slick-initialized .slick-slide {
		display: block;
	}
	.slick-loading .slick-slide {
		visibility: hidden;
	}
	.slick-vertical .slick-slide {
		display: block;
		height: auto;
		border: 1px solid transparent;
	}
	.slick-arrow.slick-hidden {
		display: none;
	}
	/* slick styles */

	#quotescar {
		padding-top: 25px;
		background: black;
	}

	#quotescar .leader_container_carousel {
		max-width: 920px;
		margin: auto;
		position: relative;
	}


	#quotescar .slick-dots{
		display: flex!important;
		justify-content: center;
		margin-top: 38px;  
		margin-bottom: 30px;
		font-size: 0px;
	}

	#quotescar .slick-slide img {
		width: 130px;
		height: 152px!important;
	}

	#quotescar .slick-slide .leader_li {
		position: relative;
		padding: 0px 15px;
	}

	#quotescar .leader_li_copy {
		text-align: center;
		font-size: 14px;
		color: white;
	}

	
	#quotescar .leader_li_copy.left-align {
		text-align: left;
	}

	#quotescar .leader_li_copy.right-align {
		text-align: right;
	}

	#quotescar .leader_li_copy h3 {
		font-size: 24px;
		font-weight: 700;
	}

	#quotescar .leader_li_copy p {
		font-size: 18px;
		font-weight: 100;
	}

	#quotescar .right,
	#quotescar .left {
		cursor: pointer;
		position: absolute;
		top: 16%;
		color: white;
		z-index: 1;
		font-size: 25px;
	}
	#quotescar .left {
		left: -70px;
	}
	#quotescar .right {
		right:-70px;
	}

	#quotescar .icon-chevron-left:before,
	#quotescar .icon-chevron-right:before {
		font-size: 58px;
