var tracking_data = {"is_live":false,"title":"Special Reports","article_id":null,"section":null,"topic":null,"region":null,"asset_type":"section","counted":null,"first_click_free":null,"author":null,"publish_date":null,"article_type":null,"content_type":null,"previous_article_id":null,"content_type_identifier":"landing_page"}

    // Add tracking for referring articles
    if(typeof(localStorage) !== 'undefined'){

        // Load history
        try {
            tracking_data.history = JSON.parse(localStorage.getItem('trackingHistory')) || [];
        } catch(e) {
            tracking_data.history = []
        }

        // Search through queue for last article remote ID
        for(i=0; i < tracking_data.history.length; i++){
            if(tracking_data.history[i].url === document.referrer){
                tracking_data.previous_article_id = tracking_data.history[i].remoteId
            }
        }

        //Clean out queue
        if(tracking_data.history.length > 10){
            tracking_data.history.shift()
        }

        //Only refer acticles
        if(tracking_data.content_type_identifier === 'article'){
            tracking_data.history.push({
                url: window.location.href,
                remoteId: tracking_data.article_id
            })

            localStorage.setItem('trackingHistory', JSON.stringify(tracking_data.history))
        }
    }

